mapper.insert:
insert into vk_file_tag
(id,file_id,tag_id)
select null,a.file_id,#{tagId}
from vk_file a where a.parent_id=#{parentId}

insert into vk_file_tag
(id,file_id,tag_id)
select null,a.file_id,#{tagId}
from vk_file a where a.file_id=#{fileId}

mapper.delete:
  delete from vk_file_tag
  
  	
		and file_id=#{fileId}
	
	
		and tag_id=#{tagId}
