mapper.select:
SELECT A.ACCOUNT_ID,A.APPID,A.APPSECRET,A.ID,A.ACCESS_TOKEN,A.EXPIRES_IN,A.UPDATE_TIME,
	A.JSACCESS_TOKEN,A.JSEXPIRES_IN,A.JSUPDATE_TIME FROM WX_ACCOUNT A
WHERE A.ACCOUNT_ID=#{accountId}

mapper.update:
UPDATE WX_ACCOUNT SET 
	ACCESS_TOKEN =#{accessToken},
	EXPIRES_IN =#{expiresIn},
	UPDATE_TIME =#{updateTime,jdbcType=TIMESTAMP}
WHERE ACCOUNT_ID=#{accountId}

UPDATE WX_ACCOUNT SET 
	JSACCESS_TOKEN = #{jsaccessToken},
	JSEXPIRES_IN = #{jsexpiresIn},
	JSUPDATE_TIME = #{jsupdateTime,jdbcType=TIMESTAMP}
WHERE ACCOUNT_ID=#{accountId}
