mapper.select:
select a.article_id,a.account_id,a.title,a.create_time,a.create_user,a.update_time,a.updata_user from wx_article a,wx_account b,td_m_depart t
a.account_id=b.account_id and b.depart_id=t.depart_id and t.depart_frame like ${priFrame}
ORDER BY a.update_time desc

select *
from wx_article a

	
		and a.article_id=#{article_id}
